package packetproxy.common;

import net.arnx.jsonic.JSON;
import org.junit.Test;

import java.util.List;
import java.util.Map;

import static org.junit.Assert.assertTrue;

public class GRPCTest {
    @Test
    public void testGRPCWebRequest() throws Exception {
        String data = "AAAAAA4KDHRlc3QgbWVzc2FnZQ==";
        List<GRPCMessage> messages = GRPCMessage.decodeMessages(data);
        List<Map<String, Object>> json = JSON.decode(JSON.encode(messages));
        assertTrue(data.equals(GRPCMessage.encodeMessages(json)));
    }

    @Test
    public void testGRPCWebResponse() throws Exception {
        String data = "";
        List<GRPCMessage> messages = GRPCMessage.decodeMessages(data);
        List<Map<String, Object>> json = JSON.decode(JSON.encode(messages));
        assertTrue(data.equals(GRPCMessage.encodeMessages(json)));
    }

    @Test
    public void testLongGRPCWebRequest() throws Exception {
        String data = "";
        List<GRPCMessage> messages = GRPCMessage.decodeMessages(data);
        List<Map<String, Object>> json = JSON.decode(JSON.encode(messages));
        assertTrue(data.equals(GRPCMessage.encodeMessages(json)));
    }

    @Test
    public void testLongGRPCWebResponse() throws Exception {
        String data = "";
        List<GRPCMessage> messages = GRPCMessage.decodeMessages(data);
        List<Map<String, Object>> json = JSON.decode(JSON.encode(messages));
        assertTrue(data.equals(GRPCMessage.encodeMessages(json)));
    }

}
