package packetproxy.quic.value.frame;

import static org.assertj.core.api.Assertions.assertThat;
import static org.junit.jupiter.api.Assertions.assertArrayEquals;

import org.apache.commons.codec.binary.Hex;
import org.junit.jupiter.api.Test;

class CryptoFrameTest {

	@Test
	public void parse後getBytesで元に戻ること() throws Exception {
		byte[] testData = Hex.decodeHex(
				""
						.toCharArray());
		CryptoFrame cryptoFrame = CryptoFrame.parse(testData);
		System.out.println(cryptoFrame);
		assertArrayEquals(testData, cryptoFrame.getBytes());
	}

	@Test
	public void equalsが正常に動作すること() throws Exception {
		byte[] data1 = Hex.decodeHex(
				""
						.toCharArray());
		byte[] data2 = Hex.decodeHex(
				""
						.toCharArray());
		CryptoFrame cryptoFrame1 = CryptoFrame.parse(data1);
		CryptoFrame cryptoFrame2 = CryptoFrame.parse(data2);
		assertThat(cryptoFrame1).isEqualTo(cryptoFrame2);
	}
}
